'use strict';
$(function() {
    var a = document.getElementById('links'),
    b = document.getElementById('link1'),
    c = document.getElementById('link2');
    if (800 < b.offsetWidth) {
        var e = function() {
            0 <= a.scrollLeft - c.offsetWidth ? a.scrollLeft -= b.offsetWidth: a.scrollLeft++
        };
        c.innerHTML = b.innerHTML;
        var d = setInterval(e, 100);
        a.onmouseout = function() {
            d = setInterval(e, 100)
        },
        a.onmouseover = function() {
            clearInterval(d)
        }
    }
    $('.js-contac').click(function() {
        $('.contac-fexid').fadeIn(500)
    }),
    0 < $('.contac-settime').size() && setTimeout(function() {
        $('.contac-fexid').fadeIn(500)
    },
    8e3),
    $('.contac-fexid .close').click(function() {
        $('.contac-fexid').fadeOut(500)
    })
}),
$(function() {
    $('.gb-aside .asidenav-btn').click(function() {
        $('.gb-aside .asidenav-btn').fadeOut(),
        $('.gb-aside .home ,.gb-aside .nav').fadeIn()
    }),
    $('.header .close').click(function() {
        $(this).addClass('active')
    });
    var a = $(window).width(),
    b = 60;
    1460 < a && (b = 80),
    $('.gb-aside .nav li.server').hover(function() {
        $(this).find('.slide-nav').css(left, b)
    },
    function() {
        $(this).find('.slide-nav').css(left, -120)
    })
}),
$(document).ready(function() {
    $('body').addClass('loaded'),
    $('.loading').fadeOut(100),
    onloaded()
});
function onloaded() {
    for (var b, c, d, e, f, a = Math.floor(10 * Math.random()) + 2, g = 1; g <= a; g++) c = Math.floor(100 * Math.random()),
    b = Math.floor(10 * Math.random()),
    d = 200 * Math.floor(10 * Math.random()),
    f = -1 * (d / 2),
    e = '<div class=\'sp-animation\' style=\'left:' + c + '%;top:' + f + 'px;\'><div class=\'obox\' style=\'width:' + d + 'px;height:' + d + 'px;animation-delay:' + b + 's\'></div></div>',
    $('.sp-animated').append(e);
    0 < $('.gotop').size() && ($(window).bind('scroll',
    function() {
        100 < $(window).scrollTop() ? $('.gotop').stop().animate({
            opacity: 1,
            bottom: 33
        }) : $('.gotop').stop().animate({
            opacity: 0,
            bottom: 150
        })
    }), $('.gotop').bind('click',
    function() {
        $('html,body').animate({
            scrollTop: 0
        })
    }))
}